import React, { useState } from 'react';
import axios from 'axios';
import './ChatWidget.css';
import MessageBubble from './MessageBubble';


const ChatWidget = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [messages, setMessages] = useState([
    { sender: 'bot', text: 'Hello! I’m ThinkLab Bot. Ask me anything about ThinkLab Group.' }
  ]);
  const [userInput, setUserInput] = useState('');
  const [loading, setLoading] = useState(false);

  const toggleChat = () => setIsOpen(!isOpen);

  const sendMessage = async () => {
    if (!userInput.trim()) return;

    const userMessage = { sender: 'user', text: userInput };
    setMessages(prev => [...prev, userMessage]);
    setLoading(true);

    try {
      const res = await axios.post(
        'https://23ae887b-f1ac-442d-b2d0-0a6e75c0771a-00-1xssic8hb4mg4.spock.replit.dev/chat', // 🔁 Replace with your actual URL
        { message: userInput }
      );
      const botMessage = { sender: 'bot', text: res.data.reply };
      setMessages(prev => [...prev, botMessage]);
    } catch (error) {
      setMessages(prev => [...prev, { sender: 'bot', text: '⚠️ Sorry, something went wrong. Please try again.' }]);
    } finally {
      setUserInput('');
      setLoading(false);
    }
  };

  const handleKeyPress = e => {
    if (e.key === 'Enter') sendMessage();
  };

  return (
    <>
      <div className="chat-icon" onClick={toggleChat}>💬</div>
      {isOpen && (
        <div className="chat-box">
          <div className="chat-header">
            <strong>ThinkLab Bot</strong>
            <span onClick={toggleChat}>&times;</span>
          </div>
          <div className="chat-messages">
          {messages.map((msg, i) => (
  <MessageBubble key={i} sender={msg.sender} text={msg.text} />
))}

            {loading && <div className="chat-bubble bot">Typing...</div>}
          </div>
          <div className="chat-input">
            <input
              type="text"
              value={userInput}
              onChange={e => setUserInput(e.target.value)}
              onKeyPress={handleKeyPress}
              placeholder="Ask me anything..."
            />
            <button onClick={sendMessage}>Send</button>
          </div>
        </div>
      )}
    </>
  );
};

export default ChatWidget;
